var express = require('express');
var router = express.Router();
var ctrlCoffeeLists = require('../controllers/coffeeLists');

router.get('/coffeeLists',ctrlCoffeeLists.getCoffeeLists);
router.get('/coffeeLists/:id',ctrlCoffeeLists.getCoffeeList);
router.post('/coffeeLists/', ctrlCoffeeLists.createCoffeeList);

module.exports = router;